package Input;

public class ASCII {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1='a';
		int number1=ch1;
		char ch2='b';
		int number2=ch2;
		int sum=number1+number2;
		System.out.println("Sum of two letters a and b with their ASCII value is = "+sum);
	}

}
